commits.content:
@tailwind base;
@tailwind components;
@tailwind utilities;



.hero{
  background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('../src/assets/9f3fcd6afeec4ed3926e5359386d174b.jpg');
  background-size: cover;
background-position: center center ;
background-repeat: no-repeat;

}

@media (max-width: 768px) {
  .progress-circle-container {
    width: 250px;
    height: 250px;
  }

  .progress-circle-container svg {
    width: 100%;
    height: 100%;
  }

  .progress-circle-container .text {
    font-size: 10px;
  }
}

@font-face {
  font-family: "Peyda-Black";
  src: url("./assets/font/Peyda/01-Standard/TTF/Peyda-Regular.ttf") format("truetype");
  font-weight: normal;
  font-style: normal;
}

body {
  font-family: "Peyda-Black", sans-serif;
  direction: rtl;
  height: 100%;
}

@layer base {
  @media (min-width: 1536px) {
    .container {
      max-width: 1351px !important;
    }
  }
}

.swiper {
  width: 100%;
  height: 100%;
}

.swiper-slide {
  text-align: center;
  font-size: 18px;

  display: flex;
  justify-content: center;
  align-items: center;
}

.swiper-slide img {
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.swiper-pagination-bullet {
  background-color:  #FDD700 !important;
  opacity: 0.5;
}


.swiper-pagination-bullet-active {
  background-color:  #FDD700!important;
  opacity: 1;
}




.product-card {
  position: relative;
  overflow: hidden;
  background-color: #2d3748; 
  border-radius: 0.375rem; 
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); 
  transition: transform 0.5s;
   background-color:rgba(0, 0, 0, 0.5); 
}

.product-card:hover {
  transform: scale(1.05);
  cursor: pointer;
}

.product-image {
  object-fit: cover;
  width: 100%;
  height: 100%;
}

.product-overlay {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: rgba(0, 0, 0, 0.5);
}



.custom-calendar {
  background-color: #1c1c1e; 
  color: white; 
  border-radius: 12px; 

  padding: 10px;
  font-family: "Vazir", sans-serif; 
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
}

.custom-calendar .react-calendar__tile {
  background: none;
  color: white;
  border-radius: 8px;
  padding: 10px;
  height: 50px;
  width: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.3s ease;
}

.custom-calendar .react-calendar__tile:hover {
  background-color: #343434; 
  cursor: pointer;
}

.custom-calendar .react-calendar__tile--active {
  background-color: #FFB703; 
  color: black; 
}

.custom-calendar .react-calendar__month-view__weekdays {
  color: #FFD700; 
  font-weight: bold;
}

.custom-calendar .react-calendar__navigation button {
  color: white; 
  background: none;
  border: none;
  font-size: 18px;
  transition: color 0.3s ease;
}

.custom-calendar .react-calendar__navigation button:hover {
  color: #FFB703; 
}
.custom-calendar {
  background-color: #1e1e1e !important;
  border-radius: 10px;
}

.custom-calendar .Calendar__day {
  background-color: #2a2a2a !important;
  color: #fff !important;
  border-radius: 8px;
}

.custom-calendar .Calendar__day.-selected {
  background-color: #ffd700 !important; 
  color: #000 !important;
}

.custom-calendar .Calendar__monthArrow {
  filter: invert(1);
}
 .Calendar__yearSelectorItem  {
  background-color: #333;
}
.Calendar__monthSelectorItem{
  background-color: #333;
}

/* تنظیم ارتفاع برابر برای موبایل */
.product-card {
  height: 20rem; /* مقدار ثابت برای موبایل */
}

/* تنظیمات برای حالت دسکتاپ */
@media (min-width: 640px) { /* sm: */
  .product-card.child-1 {
    height: 30rem;
    grid-row: span 3 / span 3;
  }

  .product-card.child-2 {
    height: 19rem;
    grid-row: span 2 / span 2;
  }

  .product-card.child-3,
  .product-card.child-4 {
    height: 24rem;
    grid-row: span 1 / span 1;
  }
}

/* برای موبایل */
@media (max-width: 640px) {
  .custom-calendar {
    max-width: 260px; /* کوچک‌تر شدن تقویم */
 /* حذف بک‌گراند */
    padding: 5px; /* کاهش فضای داخلی */
    box-shadow: none;
  }
}
.circle-container {
  position: relative;
  width: 60vw;
  max-width: 300px;
  aspect-ratio: 1;
  margin: auto;
}

.circle-text {
  font-size: 1.2rem;
  color: white;
}

.circle-label {
  position: absolute;
  font-size: 0.7rem;
  color: white;
  text-align: center;
  width: 80px;
}

/* موقعیت‌ها روی دایره */
.label-0 { top: 2%; left: 50%; transform: translateX(-50%); }
.label-1 { top: 18%; left: 85%; transform: translate(-50%); }
.label-2 { top: 50%; left: 100%; transform: translate(-50%, -50%); }
.label-3 { bottom: 18%; left: 85%; transform: translate(-50%); }
.label-4 { bottom: 2%; left: 50%; transform: translateX(-50%); }
.label-5 { bottom: 18%; left: 15%; transform: translate(-50%); }
.label-6 { top: 18%; left: 15%; transform: translate(-50%); }

/* واکنش‌گرایی */
@media (max-width: 768px) {
  .circle-container {
    width: 80vw;
    max-width: 250px;
  }
  .circle-label {
    font-size: 0.6rem;
    width: 60px;
  }
  .circle-text {
    font-size: 1rem;
  }
}

@media (max-width: 480px) {
  .circle-label {
    font-size: 0.55rem;
    width: 50px;
  }
}
